import React, { useState } from 'react';
import { FaStar } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const products = [
  { id: 1, name: 'keyboard', price: '$199', rating: 4.5, image: 'data:image/jpeg;base64,' },
  { id: 2, name: 'Headphoe', price: '$249', rating: 4.0, image: 'data:image/jpeg;base64,' },
  { id: 3, name: 'Mouse', price: '$179', rating: 5.0, image: 'data:image/jpeg;base64,' },
  { id: 4, name: 'Airbuds', price: '$299', rating: 3.5, image: 'data:image/jpeg;base64,' },
  { id: 5, name: 'Moniter', price: '$159', rating: 4.2, image: 'data:image/jpeg;base64,' },
  { id: 6, name: 'Apple Watch', price: '$219', rating: 4.8, image: 'data:image/jpeg;base64,' },
  { id: 7, name: 'Product 7', price: '$189', rating: 4.1, image: 'https://via.placeholder.com/150' },
  { id: 8, name: 'Product 8', price: '$269', rating: 3.9, image: 'https://via.placeholder.com/150' },
  { id: 9, name: 'Product 9', price: '$209', rating: 4.7, image: 'https://via.placeholder.com/150' },
  { id: 10, name: 'Product 10', price: '$229', rating: 4.3, image: 'https://via.placeholder.com/150' },
];

const Product = () => {
  const [visibleCount, setVisibleCount] = useState(5);
  const navigate = useNavigate();

  const handleShowAll = () => {
    setVisibleCount((prev) => Math.min(prev + 5, products.length));
  };

  const handleProductClick = (productId: number) => {
    navigate(`/product/${productId}`);
  };

  return (
    <section className="px-4 py-12 max-w-7xl mx-auto font-sans">
      <div className="mb-6">
        <div className="flex flex-col">
          <div className="flex items-center mb-1">
            <div className="w-3 h-7 bg-red-500 rounded mr-2"></div>
            <span className="text-red-500 font-semibold text-sm">Today's</span>
          </div>
          <h2 className="text-3xl font-bold text-black">Flash Sales</h2>
        </div>
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
        {products.slice(0, visibleCount).map((product) => (
          <div
            key={product.id}
            className="flex flex-col bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-200 hover:scale-105 group h-full min-w-[120px] max-w-full min-h-[260px] sm:min-h-[280px] md:min-h-[320px] lg:min-h-[360px] xl:min-h-[250px] cursor-pointer"
            onClick={() => handleProductClick(product.id)}
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-40 sm:h-44 md:h-48 lg:h-52 xl:h-56 object-cover"
            />
            <button
              className="w-full mb-2 bg-black text-white px-4 py-2 font-semibold text-sm shadow-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200"
            >
              Add to Cart
            </button>
            <div className="w-full px-4 flex flex-col items-start">
              <span className="text-sm font-bold mb-1 text-red-600">{product.price}</span>
              <h3 className="text-sm font-semibold mb-1">{product.name}</h3>
              <div className="flex items-center mb-2">
                {[...Array(5)].map((_, i) => (
                  <FaStar
                    key={i}
                    className={`text-yellow-400 text-sm ${i < Math.floor(product.rating) ? '' : 'opacity-30'}`}
                  />
                ))}
                <span className="ml-2 text-xs text-gray-500">{product.rating}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
      {visibleCount < products.length && (
        <div className="flex justify-center mt-8">
          <button
            onClick={handleShowAll}
            className="bg-red-600 text-white px-6 py-2 rounded-full font-semibold text-lg shadow hover:bg-red-700 transition"
          >
            Show More
          </button>
        </div>
      )}
    </section>
  );
};

export default Product;
